void mainImage( out vec4 O,  vec2 v ) {

    v *= 10./iResolution.xy;      // window mapped to (0,0) - (10,10)
    float s = 1.;

    if (v.x>5.) v.x *= s = 1.;    // right side. on my machine, gives 1.
    if (v.x>5.) v.x *=(s = 1.);   //    not better
  //if (v.x>5.) v.x *= s     ;    //    works correctly
  //if (v.x>5.) v   *= s = 1.;    //    works correctly
    else        v.x *=     1.;    // left side

    O = vec4( fract(v.x) , v.x == 1. , 0,0 ); // should draw red vertical strips
}
