void mainImage(out vec4 fragColor,vec2 fragCoords)   {
    vec2  U = fragCoords - .5*iResolution.xy,                             
          w = 50.+ (U-U);
        //w = vec2(50.);         //  works       
    float v;

#if 1
    if (U.x<0.) v = smoothstep(-w.y,w.y, U.y );     // works
    else        v = smoothstep(-w  ,w  , U   ).y;   //  bug !  <<<---
 // else        v = smoothstep(-w.y,w.y, U   ).y;   // works
#else
    if (U.x<0.) v = smoothstep(-w.y,0.,  U.y );     // works
    else        v = smoothstep(-w , R-R, U   ).y;   // works   
#endif

    fragColor = vec4(v);
}
