void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec2 r = vec2( fragCoord.xy +sin(.05 * iTime)*2.0 *iResolution.xy );

	r = 2.0 * r.xy / iResolution.xy;
   float colorwave=sin(.5 * iTime)*4.0;
    vec3 col1 = vec3 (0.7,0.9,.2*colorwave);
    vec3 col2 = vec3 (0.3,0.8,.5*colorwave);
     vec3 col3 = vec3 (0.1,0.9,.4*colorwave);
    vec3 pixi;

    float width = (sin(.05 * iTime)*2.0);
    float width2 = sin(cos(.06 * iTime)*6.0);
    float mody = dot(width*width2,sin(cos(r.x+iTime)*10.));
    float mody2 = dot(width*width2,sin(cos(r.x+iTime*.03)*20.));
    if(sin(sin(iTime+r.x*.5))/r.y> mody){

        pixi = col1;

    	}
    else {

        pixi = col2;

        }
      if(sin(sin(iTime+r.y*.4))/r.x < mody2){

        pixi = col3;

    	}

    fragColor = vec4(pixi,1.0);
}
