void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = mod (fragCoord * (2.01 - sin(iTime * .1) * 2.0), 256.0);

    // Time varying pixel color
    float color = 0.0, m = 128.0;
    for (int i=0; i<8; i++) {
        if ((uv.x - m) * (uv.y - m) < 0.0) {
            color += m;
        }
        uv = mod (uv, m);
        m *= .5;
    }

    // Output to screen
    fragColor = vec4(vec3(color/256.0),1.0);
}
