float flower(vec2 origin, float resz, float petals, float angs, float expon, float c_lol, float thicc){
	float mandala = 0.;

    vec2 polar = vec2(atan(origin.x, origin.y), length(origin));
    origin = vec2(polar.x/6.2831*petals-iTime*angs, polar.y*sin(iTime));

    float x = origin.x*resz;
    float m = c_lol - pow(min(fract(x), fract(1.-x)),expon);

	mandala = smoothstep(0., .03, m-origin.y)-smoothstep(0., .03, m-thicc-origin.y);

    return mandala;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 st = vec2(atan(uv.x, uv.y), length(uv));

    vec3 Mandala = flower(uv, 5., 2., .1, 2., .25, .1)*vec3(1., .7, .6)
        		 + flower(uv, 3., 5., -.3, 1., .5, .05)*vec3(.07, .78, .07);
        		 //+ flower(uv, 8., );

    // Output to screen
    fragColor = vec4(Mandala, 1.);
}
