precision highp float;
#define resolution iResolution.xy
#define time iTime
#define pi 3.14159
vec3 render(vec2 fc)
{
    vec2 p=(gl_FragCoord.xy-.5*resolution)/
        max(resolution.x,resolution.y);
    vec2 q=p;
    float vig=smoothstep(.5,.2,length(p));
    float cnt=smoothstep(.0,.075,length(p));
    vec3 col=vec3(.0);
    float r=10.;
    float t=fract(time/r);
    t*=pi*2.;
    p*=mat2(.5,.0,.0,2.);
    p*=25.;
    float a=.0;
    a+=length(p)*4.;
    float f=fwidth(a);

    vec2 d = vec2(0.);
    d.x=cos(p.x*p.x+t*2.-a);
    d.y=sin(p.y*p.y-t*3.+a);

    d=smoothstep(f*2.,0.,abs(d-1.));
    col+=d.xyy;
    col=clamp(col,.0,1.);
    col*=vig;
    col*=smoothstep(.2,-.5,abs(q.y));
    return col;
}

void mainImage( out vec4 fragcol, in vec2 coord )
{
    vec2 fc = coord.xy;
    vec3 col=render(fc);
    col=pow(col,vec3(1./2.2));
    fragcol=vec4(col,1.);
}
