void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-0.5;

    vec2 sq = vec2(uv.x/(iResolution.y/iResolution.x),uv.y);

    float dist = distance(sq,vec2(cos(iTime),sin(iTime))/3.0);

    // Time varying pixel color
    vec3 col = vec3(sin((sq.x+sq.y*max(sin(iTime),cos(iTime*1.2)))*32.0),cos((sin(iTime*1.2)+sq.y+sin(iTime+sq.x*2.0))*8.0),sin(dist*31.0-iTime*2.0));

    if (col.r>0.8&&col.g>0.8)
    {
    vec3 col2 = vec3(col.r*0.,col.g*0.2,1.0);
    col = mix(col,col2 - col.b,mix((col.g-0.8)*3.0,(col.r-0.8)*3.0,0.5));
    }
    col = mix(-col,col,sin(iTime)/2.0+0.5)+vec3(0.2,0.2,0.5);
    // Output to screen
    fragColor = vec4(col,1.0);
}
