void mainImage( out vec4 c, in vec2 f )
{
    vec2  p = (2.*f-iResolution.xy)/iResolution.y;
    	  p = vec2(log(length(p.xy)),atan(p.x,p.y)+iTime/3.0)*4.0;

    float b = sin(p.x*(cos(iTime/2.)+2.0)*2.+p.y)+sin(p.y);

    c.rgb   = length(b)*vec3(f.x*p.x*.1, f.y*p.x*.5, f.y*(1.+sin(p.x)/1.4)/2.5)*.00125;
    c.rgb  += pow(c.b,2.75);
}
