#define T(uv) texture(iChannel0,uv)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy);


    float f = length(uv  - 0.5);
    fragColor.x = T(uv + f*0.005).x;
    fragColor.y = T(uv -f*0.005).y;
    fragColor.z = T(uv-f*0.005).z;

    fragColor = pow(fragColor, vec4(0.4545 ));
}

#define pi acos(-1.)
#define tau (2.*pi)
#define rot(x) mat2(cos(x),-sin(x),sin(x),cos(x))
#define S(a, b, t) smoothstep(a, b, t)

// inigo quilez
float sdTri(  vec2 p, float s )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - s;
    p.y = p.y + s/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0, 0.0 );
    return -length(p)*sign(p.y);
}

float sdCircle( vec2 p, float r )
{
  return length(p) - r;
}

//from iq : https://iquilezles.org/articles/distfunctions
float smin( float d1, float d2, float k ) 
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord  )
{
 vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;    
 uv *= 2.;
 vec3 col = vec3(0);
 float d = 10e6;
 float triA;
 float dp = dot(uv*1.,uv*1.2);
 uv /= dp;
 uv.xy=sin(uv-vec2(-iTime*pi*1.,-iTime*tau*.1+iTime*0.1)).xy;
 vec2 uv2 = vec2(vec2(uv.x*1.,uv.y*1.)*rot(iTime*pi));
 triA = sdTri(uv2, .8);
 vec2 uv3 = uv2;
 uv2.x = abs(uv2.x)-0.11;
 float c = sdCircle(vec2(uv2.x,uv2.y-0.45),.03);
 float c2 = sdCircle(vec2(uv3.x,uv3.y-0.5),.2);
 float c3 = sdCircle(vec2(uv2.x,uv2.y),.74);
 uv3 = vec2(uv3.x,uv3.y-.24);
 float smile = sin(uv.y*4.)*3.+2.+3.;
 uv3.y *= 2.;
 uv3.y -= uv3.x*uv3.x*2.*smile;
 uv3.x *= mix(.1, .6, smile);
 float mou = length(uv3)-.2;
 triA = smin(triA,c3,-.15);
 triA = max(triA, -mou);
 triA = max(triA,-c);
 d = min(d, triA);
 col += smoothstep(0.05,0./iResolution.y,-d);
 col = 1. - col;
 fragColor = vec4(col,1.0);
}
