void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    int strips = 150;
    float width = iResolution.x / float(strips);

    float curStrip = floor(fragCoord.x / width);
    float center = curStrip * width + width*.5;
    float height = (sin(.025 * iTime * (curStrip+1.))*.5+.5) * iResolution.y;

    vec2 delta = fragCoord - vec2(center, height);
    float d = .5*dot(delta, delta)/width;    
    vec3 col = vec3(d);

    fragColor = vec4(col,1.0);
}
