mat2 rebase(vec2 v)
{
	return mat2(v.x, 0.0, 0.0, v.y);   
}

mat2 rotate2d(float _angle)
{
	return mat2(cos(_angle),-sin(_angle), sin(_angle),cos(_angle));
}

vec2 bottomLeft = vec2(0.0, 0.0);
vec2 topRight   = vec2(0.5, 0.5);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= vec2(0.5);

    //topRight *= rebase(vec2(sin(uv.y - iTime * uv.x), tan(uv.x + iTime * uv.y)));
    topRight *= rotate2d(tan(uv.y - (10000000000.0) * uv.x));


	float interpX = smoothstep(uv.x, bottomLeft.x, bottomLeft.x - bottomLeft.x/2.0) - smoothstep(uv.x, topRight.x, topRight.x - topRight.x/2.0);
	float interpY = smoothstep(uv.y, bottomLeft.y, bottomLeft.y - bottomLeft.y/2.0) - smoothstep(uv.y, topRight.y, topRight.y - topRight.y/2.0);

    float interp = interpX * interpY;

    uv *= ((1.0 - interp) * rebase(vec2(1.0)) + interp * rebase(vec2(cos(iTime) + uv.x, sin(iTime) + uv.y))); 

    uv += vec2(0.5, 0.5);


    vec4 tex = texture(iChannel0, uv);


    // Output to screen
    fragColor = tex;
}
