// scroll fft

void mainImage( out vec4 f, in vec2 g )
{
    vec2 s = iResolution.xy;
    vec2 uv = g/s;

    if (uv.y<0.01){
      vec4 v = texture(iChannel0, vec2(uv.x,0.29)); //vec2(exp(-uv.x*3.0),0.29));      
      float fft=(v[0]*v[0])*1.5;
      f = vec4(fft,fft,fft,1.0);

    }else{
      float b=0.005; // scrool speed
      vec4 h = texture(iChannel1, vec2(uv.x,uv.y-b));
      f=h;
    }
}

void mainImage( out vec4 f, in vec2 g )
{
    vec2 s = iResolution.xy;
    vec2 uv = g/s;

    float sum=0.0;
    int N=0;
    for (float x=0.0;x<1.0;x+=0.02){
      vec4 v1 = texture(iChannel0, vec2(x,uv.x));  
      vec4 v2 = texture(iChannel0, vec2(x,uv.y));                            

       float m = v1.x*v2.x;
       sum += m*m;
       N+=1;
    }                         

    float fft = 8.0*sum/float(N);

    //f = vec4( fft,fft,fft,1.0); 
    f = vec4( sin(fft*3.141*2.5), sin(fft*3.141*2.0),sin(fft*3.141*1.0),1.0);


}
