// See Common for settings

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int state = int(texelFetch(iChannel0, ivec2(fragCoord), 0).x);
    fragColor = state == 0 ? deadColor : mix(liveColor, fadeColor, float(state - 1) / (float(stateCount) - 1.99));
}

// hash13 from https://www.shadertoy.com/view/4djSRW
float hash13(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

int st(ivec2 pos, int dx, int dy) {
    return int(texelFetch(iChannel0, (pos + ivec2(dx, dy) + ivec2(iResolution.xy)) % ivec2(iResolution.xy), 0).x);
}

int stv(ivec2 pos, int dx, int dy) {
    return int(st(pos,dx,dy) == 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 pos = ivec2(fragCoord);
    int state = st(pos, 0, 0);
    if (iFrame == 0) {
        state = int(hash13(vec3(fragCoord, fragCoord.x + iDate.w)) + initDensity);
        state *= int(length(fragCoord - iResolution.xy * .5) < initRadius * iResolution.x);
    } else {
        int neighborTotal =
            stv(pos,-1,-1) +
            stv(pos,-1,0) +
            stv(pos,-1,1) +
            stv(pos,0,-1) +
            stv(pos,0,1) +
            stv(pos,1,-1) +
            stv(pos,1,0) +
            stv(pos,1,1);
        int transition = stateTransitions[9 * min(state, 1) + neighborTotal];
        if (state == 0) {
            state = transition;
        } else if (state == 1) {
            state = 2 - transition;
        } else {
            state = state + 1;
        }
        state = state % stateCount;
    }
    fragColor = vec4(state);
}

// Current rule: B2/S/3

const int stateTransitions[18] = int[18](
//  0 1 2 3 4 5 6 7 8
    0,0,1,0,0,0,0,0,0, // B
    0,0,0,0,0,0,0,0,0  // S
);
const int stateCount = 3;

const vec4 deadColor = vec4(0.0, 0.0, 0.0, 1.0);
const vec4 liveColor = vec4(1.0, 1.0, 0.0, 1.0);
const vec4 fadeColor = vec4(1.0, 0.0, 0.0, 1.0);

const float initDensity = 0.1;
const float initRadius = 0.1;
