void X (inout vec4 Q, vec2 U, vec2 u) {
    vec4 a = A(u), q = A(Q.xy);
    if (length(U-a.xy)<length(U-q.xy))
        Q.xy = u;
}
void Z (inout vec4 Q, vec2 U, vec2 u) {
    vec4 a = A(u), q = A(Q.zw);
    if (length(U-a.xy)<length(U-q.xy))
        Q.zw = u;
}
