    convolution 
        X(Q,U,C(U+vec2(x,y)).xy);

    convolution2 
        X(Q,U,A(Q.xy+vec2(x,y)).xy);
    X(Q,U,Q.zw);
    #define N 12
    if (iFrame%N==0) Q.zw = U;
    else {
        float k = exp2(float(N-1-(iFrame%N)));
    	Z(Q,U,C(U+vec2(0,k)).zw);
    	Z(Q,U,C(U+vec2(k,0)).zw);
    	Z(Q,U,C(U-vec2(0,k)).zw);
    	Z(Q,U,C(U-vec2(k,0)).zw);
    }

    if (iFrame < 1) {
        Q = vec4(U,0,0);
    }

}
