void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         M = iMouse.xy;
    if ( length(M)<20. ) M = floor( R/2. + R/2.01 *cos( floor(iTime*4.) ) );
                            // floor is crucial 

    O = vec4( fract(sin(dot( U, vec2(12.9898, 78.233) )) * (M.x/R.x)*1e5 ) ); // 43758.5453)
    if ( length(M-U) < 10. ) O = vec4(1,0,0,1);
}
