void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.0;
    fragColor = vec4(mod(uv,10.0*sound(iTime+uv.x)),1.0,1.0);
}

// http://countercomplex.blogspot.jp/2011/10/algorithmic-symphonies-from-one-line-of.html
// http://wurstcaptures.untergrund.net/music/

vec2 sound1(int t)
{
    t = ((t/(((t/4096/3)%3)+((t/4096/2)%2))))|t>>4&t>>5|t>>6;
    return vec2(float(t & 0xff - 128) / 128.);
}

vec2 sound(float time){
    time *= 16000.0;
    float factor = 1.0;
    vec2 result = vec2(0.0);
    for(int i = 0; i < 4; i++){
    	result += sound1(int(time*factor))/factor;
        factor *= (1.5+sin(time/10.0)/5.0);
    }
    return result/10.0;
}

//sound is in Common tab

vec2 mainSound( in int samp, float time )
{
    return sound(time);
}
