//Longer version, but also a visualizer (322 characters): 
void mainImage( out vec4 c, in vec2 u )
{
    vec2 p = (u-0.5*iResolution.xy)/iResolution.y;

    int t = int(abs(p.x * p.y)*200.0+10.);
	float m  = texelFetch( iChannel0, ivec2(t,0), 0 ).x, 
    	r=length(p)*2.,a = atan(p.y,p.x), sp = length(p)-2.,
        f = cos(a*12.+iTime*5.+m)-abs(cos(a*5.-m))*1.5+.1;
    c = vec4(vec3(1.-smoothstep(f,f+.9,r))/sp*vec3(p*2.,-.5-p.x)*3.,1.);
}

