vec2 sound1(int t)
{
    t = (t>>(24*(t/(2*2*2*2*2*2*2*2*2*2*2*2))))|(t>>(8+(t/(2*2*2*2*2*2*2*2*2*2*2*2*2*2*2*2*2))))|(t*2);
    return vec2(float(t & 0xff - 128) / 128.);
}

vec2 mainSound( in int samp,float time){
    time *= 4000.0;
    float factor = 1.5;
    vec2 result = vec2(0.0);
    for(int i = 0; i < 4; i++){
    	result += sound1(int(time*factor))*factor;
        factor *= 1.5;
    }
    return result/100.0;
}
