void FocusLine(vec2 uv,float a,float b,out float Out)
{
    vec2 p=uv;
    float r = length(p);
 	r = 0.7 * r - 0.7;
 	float aa = atan(uv.y, uv.x);
 	aa = abs(cos(a * aa) + sin(b * aa));
 	float d = aa - r;
 	Out = smoothstep(0.1, 0.4,clamp(d,0.0,1.0));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv =  ( 2.*fragCoord - iResolution.xy ) / iResolution.y;
 	vec2 p=fragCoord / iResolution.xy;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float n, a=50.,b=20.;
 	FocusLine(uv,a,b,n);
    fragColor = texture(iChannel0, p)*n;
}
