void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-vec2(iResolution.x,iResolution.y)/2.0)/100.0;
    vec3 p;
    p = vec3(5.,5.,5.);
    vec3 s = vec3(uv.x,uv.y,1.);
    vec3 d = vec3(uv.x,uv.y,pow((1.0+uv.x*uv.x+uv.y*uv.y),.5));
    float m = 1.;
    //d+= vec3(iTime/100.,0,0);
    for (int i = 0; i<1000;i++)
    {
        float mi = 1000.;

        float x =(mod(s.x,10.0)-p.x);
        float y =(mod(s.y,10.0)-p.y);
        float z =(mod(s.z,10.0)-p.z);

        float l=pow(
            pow(x,2.0)+
            pow(y,2.0)+
            pow(z,2.0),.5); 

        if (l<mi) mi = l;
        d.x*=1.+cos(iTime/100.)/1000.;
        d.y*=1.+tan(iTime/10.)/1000.;
        //mi+=l/2.;

        if (m>mi) m = mi;
        s += d*mi/2.0;

    }
    float o = 0.;
    if (m<-.05) o = 0.99;
    // Time varying pixel color
    vec3 col =  
        vec3(pow(pow((s.x),2.0)+pow((s.y),2.0)+pow((s.z),2.0),.5)/8005.0,
             (1.-m)/2.,o);

    // Output to screen
    fragColor = vec4(col,1.0);
}
