#define SPEED 0.05
#define STEPS 25
#define STEPSIZE 250.0
#define OFFSET 15

vec3 raycast(vec2 p, vec2 uv)
{
    vec2 v = normalize(vec2(uv.x*0.5,(uv.y*0.5)+1.0))/STEPSIZE;

    vec3 c = vec3(0,0,0);

	for( int i=OFFSET; i < STEPS + OFFSET; i++ )
    {
        vec3 tex = texture(iChannel0, p + (v*float(i))).xyz;
        c += tex; 
    }

    return c/float(STEPS);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy) - 0.5;

    fragColor = vec4(raycast(vec2(0.5,fract(iTime*SPEED)),uv),1.0);
}
