// BULLSEYE SHADER

#define NUM_RINGS 6
#define HARDNESS 8.0
#define C0 vec3(0.2, 0.2, 0.2)
#define C1 vec3(1, 0, 0)

#define ASPECT vec2(16.0/9.0, 1.0)	// shadertoy preview image is 1600x900
#define CENTER vec2(0.5, 0.5) * ASPECT	// UV center

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Get the distance from this UV to the CENTER
    float dist = length(uv * ASPECT - CENTER);

    vec3 col = vec3(0,0,0);

    /* 
	// METHOD 1:
    for (int i=1; i <= NUM_RINGS; i++)
    {
        // Keep the rings scaled within the UV space, regardless of NUM_RINGS
        float fnr = float(i) / float(NUM_RINGS);
        float prev_fnr = float(i-1) / float(NUM_RINGS);

        // Alternates between 0 & 1
        float alt_ring = float(i % 2);

        // Use smoothstep to give a soft edge to the ring
        float this_ring = -smoothstep(0.498 * fnr, 0.5 * fnr, dist);

        // Make sure we don't overwrite the previous inner rings' colors
        float prev_ring = -smoothstep(0.498 * prev_fnr, 0.5 * prev_fnr, dist);
        this_ring -= prev_ring;

        // Color this ring with either C0 or C1
        col += ((alt_ring * C1) + ((1.0 - alt_ring) * C0)) * this_ring;
    }
	*/
	//-----------------------------

    // METHOD 2: simpler but not as controllable

    dist = clamp(dist, 0.0, 0.5);

    float d = sin(dist * float(NUM_RINGS + 1) * 5.0 - 1.0) * HARDNESS;

    col = d * C1;
    //------------------------------

    // Output to screen
    fragColor = vec4(col,1.0);
}
