// Fork of "Transport Dynamics" by wyatt. https://shadertoy.com/view/wdVBRR
// 2020-11-29 03:57:58

Main {
    vec4 a = A(U), b = B(U);
    Q = .8*atan(a.w+vec4(0.,.5*b.w,b.w,1));
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
float ln (vec2 p, vec2 a, vec2 b) { // returns distance to line segment for mouse input
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}
float ln (vec3 p, vec3 a, vec3 b) { // returns distance to line segment for mouse input
    return length(p-a-(b-a)*dot(p-a,b-a)/dot(b-a,b-a));
}

// Apply forces
vec2 F (vec2 u) {
    vec4 a = A(gl_FragCoord.xy + u);
    vec4 b = B(gl_FragCoord.xy + u);
	return .125*(b.w+sqrt(a.w)*abs(sqrt(a.w)-.5))*u/dot(u,u);
}
vec4 X (vec4 Q, vec2 u) {
	vec4 a = A(gl_FragCoord.xy + u);
    float f = Q.w-a.w;
    return mix(Q,a,clamp(10.*f*f,0.,1.));
}
Main {
	Q = A(U);
    if (iFrame%2==0)  {
        float e = .2+.8*Q.w;
        Q.xy -= e*F(vec2(0,1));
        Q.xy -= e*F(vec2(1,0));
        Q.xy -= e*F(vec2(0,-1));
        Q.xy -= e*F(vec2(-1,0));
        Q.xy -= e*F(vec2(1,1));
        Q.xy -= e*F(vec2(-1,1));
        Q.xy -= e*F(vec2(1,-1));
        Q.xy -= e*F(vec2(-1,-1));
        if (length(Q.xy)>1.) Q.xy = normalize(Q.xy);
        if (iFrame < 1) {
            Q = vec4(0,0,0,.5+.1*sin(U.x)*sin(U.y));
        }
    } else {
    	Q = 
        0.125*X(Q,vec2(1,0))+
        0.125*X(Q,vec2(0,1))+
        0.125*X(Q,vec2(-1,0))+
        0.125*X(Q,vec2(0,-1))+
        0.125*X(Q,vec2(1,1))+
        0.125*X(Q,vec2(1,-1))+
        0.125*X(Q,vec2(-1,-1))+
        0.125*X(Q,vec2(-1,1));
    }
    if (iMouse.z>0.&&length(U-iMouse.xy)<20.)
        Q = vec4(1,0,6,1);
    if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.)Q.xy*=0.;
}

// Conservative advect alternating x and y-direction
Main {
    Q = vec4(0);
    if (iFrame%2==0)
	for (float i = -1.; i <= 1.; i++)
    {
    	vec2 u = vec2(0,i);
        vec4 a = A(U+u-vec2(0,0.5)),
             b = A(U+u),
             c = A(U+u+vec2(0,0.5));
        float w1 = 1.+c.y-a.y;
        if (w1>0.) {
            float w = clamp(u.y+.5+c.y,-.5,.5)-
                      clamp(u.y-.5+a.y,-.5,.5);
            Q.xyz += b.w*w/w1*b.xyz;
            Q.w += b.w*w/w1;
        }
    } else 
	for (float i = -1.; i <= 1.; i++)
    {
    	vec2 u = vec2(i,0);
        vec4 a = A(U+u-vec2(0.5,0)),
             b = A(U+u),
             c = A(U+u+vec2(0.5,0));
        float w1 = 1.+c.x-a.x;
        if (w1 > 0.) {
            float w = clamp(u.x+.5+c.x,-.5,.5)-
                      clamp(u.x-.5+a.x,-.5,.5);
            Q.xyz += b.w*w/w1*b.xyz;
            Q.w += b.w*w/w1;
        }
    }
    if (Q.w > 0.) Q.xyz /= Q.w;
    if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.)Q.xy*=0.;

}

// Apply forces
vec2 F (vec2 u) {
    vec4 a = A(gl_FragCoord.xy + u);
    vec4 b = B(gl_FragCoord.xy + u);
	return .125*(b.w+sqrt(a.w)*abs(sqrt(a.w)-.5))*u/dot(u,u);
