vec2 hash( vec2 p ) {
	p = vec2(dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)));
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

vec2 randomGrad(vec2 p) {
    return normalize(hash(p));
}

float smoothstep0(float t) {
    return t * t * (3. - 2. * t);
}

float smoothstep1(float t) {
    float t3 = t * t * t;
    return 1. - 6.*t*t*t3 + 15.*t*t3 - 10. *t3;
}

float perlin(vec2 p, vec2 offset) {
    vec2 pi = floor(p);
    vec2 g = p - (pi + offset);
    return smoothstep1(abs(g.x)) * smoothstep1(abs(g.y)) * dot(g, randomGrad(pi + offset));
}

float noise(vec2 p) {
    return perlin(p, vec2(0,0))
    + perlin(p, vec2(1,0))
    + perlin(p, vec2(0,1))
    + perlin(p, vec2(1,1));
}

float fbm(vec2 n) {
    float total = 0.0;
    float tw = 0.0;
    float w = 2.;
    float a = 1.;
    float s = sin(123.456);
    float c = cos(123.456);
    mat2 r = mat2(c,s,-s,c);
    mat2 rot = mat2(1,0,0,1);
	for (int i = 0; i < 5; i++) {
		vec2 nn = (n/a) * rot;
        total += abs(noise(nn)) * a;
        //total += noise(nn) * a;
        rot = rot * r;
        tw = tw + a;
        a = a / w;
	}
	return total / tw;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //uv.y += iTime;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    col = vec3(fbm(uv * 10.));
    //col = vec3(noise(uv * 20.));
    fragColor = vec4(col,1.0);
}
