#define cycles 30

vec2 complexMul(vec2 a, vec2 b) {
	return vec2(a.x*b.x - a.y * b.y, a.x * b.y + b.x * b.y);     
}


float juliaSet(vec2 z, vec2 c) {

    float n = 0.;
    for(int i=0; i < cycles; i++) {
        z = complexMul(z, z) + c;
        n +=1.;
        if(length(z) > 8.) {
            break;
        }
    }

    return smoothstep(0., float(cycles), n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pv = vec2(uv);
	pv -= 0.5;
    pv *= 3.;
    //pv *= iResolution.x/iResolution.y;
    // Time varying pixel color
    vec3 col = vec3(0.);

	vec3 img =texture(iChannel0, uv).rgb;

    float result = juliaSet(
    	pv.xy,
        vec2(cos(iTime * 2.), sin(iTime * 3.))
    );


    vec3 img2 =texture(iChannel0, vec2(uv.x - result * 0.1, uv.y + result*0.2)).rgb;


    col += max(vec3(result), img2);

    if(length(col) == sqrt(3.0)) {
        col = 0.2/img;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
