void mainImage(out vec4 O, vec2 I)   
{
	vec4 i = modf(9e1+vec4(-I,I)/iResolution.y*9.,O);
	i *= i*(3.-i-i);
	O += dot(i*i.yzwx, fract(sin(vec4(58,57,0,1) + O.x+57.*O.y)*4e5) )-O;
}

