void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(texture(iChannel0,uv).xy*6.-vec2(0.1),1.,1.);
}

vec2 volAt(in vec2 pos){
	return (texture(iChannel0,pos/iResolution.xy).xy*2.*5.-vec2(1.));
}

vec4 vol(in vec2 vol){
	return vec4((vol+vec2(1.))/2./5.,1.,1.);
}

float smoothbump(float center, float width, float x) {
  float w2 = width/2.0;
  float cp = center+w2;
  float cm = center-w2;
  float c = smoothstep(cm, center, x) * (1.0-smoothstep(center, cp, x));
  return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(texture(iChannel0,fragCoord/iResolution.xy).x == 0.){
        fragColor=vec4(0.1,0.1,0.1,1.);
    	return;
    }
    vec2 v = volAt(fragCoord);
    fragCoord -= v*2.;
    v = volAt(fragCoord)*5.
        +volAt(fragCoord+vec2(1.,0.))
        +volAt(fragCoord+vec2(-1.,0.))
        +volAt(fragCoord+vec2(0.,1.))
        +volAt(fragCoord+vec2(0.,-1.));
    v /= 4.0001+5.;
    v += v*sin(length(v)*10.)/200.;

    vec4 m = iMouse;
    float m1 = 0.;
    if(m.z > 1.){
            m1 = smoothbump(m.x,50.,fragCoord.x) *
             smoothbump(m.y,50.,fragCoord.y);
	}
    fragColor = vol(v)+(sin(m1*30.)/10.*vec4(sin(iTime*5.),cos(iTime*5.),1.,1.));

}
