#define px 3./iResolution.y
#define PI 3.1415927
mat2 rot(float d)
{
    float s = sin(d);
    float c = cos(d);
    return mat2(c,-s,s,c);
}
float cir(vec2 p, float r)
{
    return smoothstep(0.,px,length(p)-r);
}
float sq(vec2 p, float d)
{
    return smoothstep(0.,px,length(p.x)-d)+smoothstep(0.,px,length(p.y)-d);
}
void mainImage( out vec4 c, in vec2 f )
{
    vec2 p = (2.*f-iResolution.xy)/iResolution.y+vec2(0,.15);
    float r = cir(p/vec2(.8,1.)+vec2(-0.01,1.5),1.4)-cir(p-vec2(0.0,0),.58-p.y/6.);
    float h = cir(p+vec2(0,1.5),1.5)-cir(p,.53);
    float t = 1.-cir(p-vec2(0,.52),.025);    
    p+=vec2(0.,.03);
    vec2 bp = p/vec2(1.2,1.);
    vec2 sp = (bp-vec2(0.,.2))*rot(PI/4.);
    float l = sq(sp,.1)+sq(p-vec2(0.,.45),.25);
    sp = (bp-vec2(0.01,.15))*rot(PI/4.);
    float e = clamp(1.-(sq(sp/vec2(1.,1.3),.1)+sq(p/vec2(4.,1.)-vec2(0.1,.42),.2)),0.,1.);
    c.rgb=vec3(r+t,0,0)+vec3(h);
    if(length(p-vec2(0,.25))-.2<=0.)c.rgb=c.rgb*(l+e)+vec3(0,.3,0); 
}
