void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Creates xy values https://www.shadertoy.com/view/Mscyzr
    vec2 xy = (fragCoord/min(iResolution.x,iResolution.y)-vec2(0.5*iResolution.x/iResolution.y, 0.5))*Scale;

    // Output to screen
    fragColor = vec4(TangentVisualiser(TangentFunction(xy.x, xy.y, iTime)), 1.);
}

//Values
#define Scale 10.
#define PI 3.14159265359
#define D3PI 3.14159265359/1.5

//Functions
float TangentFunction(in float x, in float y, in float Time)
{
    return x*x - y*y - sin(Time*0.5)*Scale;
    //return y*y - x*x - sin(iTime)*Scale;//Get error if you use this
}
