#define PI 3.14159265358

mat2 r2(float a) {
	float sn = sin(a), cs = cos(a);
	return mat2(cs,-sn,sn,cs);
}

vec4 hash(vec4 a) {
	return fract(abs(sin(a.ywxz*766.345)+cos(normalize(a)*4972.92855))*2048.97435+abs(a.wxyz)*.2735);
}

vec3 hsvToRGB(vec3 hsv) {
	return mix(clamp(abs(mod(hsv.x*6.0+vec3(0.,4.,2.),6.)-3.0)-1.0,0.,1.),vec3(1),hsv.y)*hsv.z;
}

float twave(float x) {
	x = fract(x);
	return x*2.-max(0.,x*4.-2.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#define time iTime
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
	vec3 s = vec3(0);
	for (float l = 15.; l < 30.; l += 0.1) {
		vec2 c = floor(uv.xy*l+time*vec2(1,1.));
		vec4 h = hash(c.xyyx);
		s += hsvToRGB(vec3(h.x,h.z*.4,max(0.,twave(l*.005+time*.1*h.y+h.y)*10.-9.)));
	}
	fragColor = vec4(pow(s*.03,vec3(1.6)),1);
}
