//adapted from https://www.shadertoy.com/view/XdXGW8


float nTime(){

return iTime*3.123;
}

vec2 hash( vec2 v )  
{
    vec2 n = vec2( 0.1141592, 0.31812123 );    
    v = v * n + n.yx;    

    vec2 h =  -1.0 + 2.0 * fract(((6.0 * nTime()) / 6.66) * n * fract( v.x * v.y * (v.x + v.y)) );  

    if(h.x>0.1){  h =  -1.0 + 2.0 * fract(((12.0 * nTime()) / 122.33) * n * fract( v.x * v.y * (v.x + v.y)) );    }

    return abs(h);
}


float noise(in vec2 p)
{
    vec2 i = floor(p);
    vec2 j = fract(p);

    vec2 k = j * j * (3.0-2.0 * j);

    float A = mix(
        dot( hash( i + vec2(0.0,0.0) ), j - vec2(0.0,0.0) ), 
        dot( hash( i + vec2(1.0,0.0) ), j - vec2(1.0,0.0) ),
        k.x);


    float B = mix( 
        dot( hash( i + vec2(0.0,1.0) ), j - vec2(0.0,1.0) ), 
        dot( hash( i + vec2(1.0,1.0) ), j - vec2(1.0,1.0) ), 
        k.x);


    float r = mix(A, B, k.y);

    return r;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 4.;
	float brightness =5.;
    vec2 uv = fragCoord.xy/iResolution.xy;

    float f;


     mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );

    f =  0.2500*noise( scale*uv ); uv = m*uv;
	f += 0.1250*noise( scale*uv ); uv = m*uv;
    f += 0.0625*noise( scale*uv ); uv = m*uv;


   f*=brightness;

	float r = f;
    float g = f;
    float b = f;

   r = sin((uv.x/1.1)*f);




    vec3 col = vec3(r,g,b);
    fragColor = vec4(col,1.0);
}
