float oneHz(){

   return(fract(iTime)*6.29);
}

float osc( float scale){

  scale *= .5;

  return sin(oneHz()) * scale + scale;
}



vec4 tiler(vec2 i){
    vec3 o = vec3(1.);


    vec2 grid = vec2(fract(i.x),fract(i.y));

    grid -=.5;

    if ( length(grid)<(osc(.8) - length( i ) / 50.)  ){
       grid.x = length(i);
       grid.y =1.; //oneHz();
    } else {
       grid = vec2(0.);         
    }


    o = vec3(grid.yx, 00.);



    o.x = oneHz()/length(i);



    return vec4(o, 1.0);

}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)*25./iResolution.y;

    vec4 col = tiler(uv);

    // Output to screen
    fragColor = col;
}
