void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    vec4 col = texture(iChannel0, uv);

    float x = col.x + 0.8*col.y + 0.4*col.z + 0.2*col.w;

    fragColor = col; //vec4(x, x, x, 1.0);
}

const vec2 NEIGHBOURS[8] = vec2[8](
    vec2(-1, -1),
    vec2(-1, 1),
    vec2(1, -1),
    vec2(1, 1),
    vec2(0, -1),
    vec2(0, 1),
    vec2(-1, 0),
    vec2(1, 0)  
);

vec2 getUV(vec2 fragCoord) {
    return fragCoord/iResolution.xy;
}

bool spacePressed() {
    return texelFetch( iChannel1, ivec2(32,0), 0 ).x > 0.5;
}

bool oneStep() {
    return texelFetch( iChannel1, ivec2(39,1), 0 ).x > 0.5;
}


bool same(vec2 a, vec2 b) {
    return abs(a.x-b.x) < 0.6 && abs(a.y-b.y) < 0.6;
}

bool get(vec2 ij) {
    return texture(iChannel0, getUV(ij)).x > 0.5;
}

int neighbours(vec2 ij) {
    int c = 0;
    for (int i = 0; i < 8; i++) {
        if (get(ij + NEIGHBOURS[i])) c++;
    }
    return c;
}

bool getNext(vec2 ij) {
    int n = neighbours(ij);
    return n == 3 || (n == 2 && get(ij));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = vec4(0.0);
    vec4 previous = texture(iChannel0, uv);

    if (iTime < 1.0) {
        col = texture(iChannel2, uv).xxxx;
    } else if (!spacePressed() && !oneStep() && same(iMouse.xy, fragCoord)) {
        col = vec4(1.0, 1.0, 1.0, 1.0);                
    } else {
        if (spacePressed() || oneStep()) {
            col = vec4(getNext(fragCoord), previous.x, previous.y, previous.z);
        } else {
            col = previous;
        }
    }

    fragColor = col;
}
