float random(in vec2 _st){
  return fract(sin(dot(_st.xy,
        vec2(12.9898,78.233)))*
      43758.5453123);
}

float random(in float x){
    return fract(sin(x)*1e4);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    vec3 color=vec3(0.,0.,0.);

    float numLines=random(st.x);
    st*=vec2(numLines,2.);
    st=vec2(fract(st.x),st.y);

    float freq=iTime/100.;
    float colTracker=step(1.,mod(st.y,2.));

    st.x-=2.*fract(iTime)*(1.-colTracker)+(1.-freq);
    st.x+=2.*fract(iTime)*colTracker+freq;

    color=vec3(step(numLines,mod(st.x,2.)));

    // Output to screen
    fragColor = vec4(color,1.0);
}
