float random (vec2 xy) {
    return fract(sin(dot(xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}


vec4 topCrop(vec4 texColor,vec2 xy,float size)
{
    float l=step(size,1.-xy.y);
    texColor=l*texColor+(1.-l)*vec4(vec3(random(xy)),1.);
    return texColor;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{

    vec2 xy = fragCoord/iResolution.xy;

    vec4 texColor=texture(iChannel0,xy);
    texColor=topCrop(texColor,xy,sin(iTime/3.));


    fragColor=texColor;
}
