// Original source: 
// https://www.shadertoy.com/view/4dcGDf

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 sceneColor = texelFetch(iChannel0, ivec2(fragCoord.xy), 0).rgb;

    vec3 skyColor = vec3(0.3*(fragCoord.y*1.5+1.0)/iResolution.y, 0.3, 0.1);
    float sky = smoothstep(0.1, 1.0, luminanceRB(sceneColor))*5.0;

    // Blend with sky
    vec3 finalColor = mix(sceneColor, sceneColor-skyColor, clamp(sky, 0.0, 1.0));

    fragColor = vec4(finalColor, 1.0);

    //fragColor = vec4(sky);
    //fragColor = vec4(luminanceRB(sceneColor.rgb), sceneColor.g, luminanceRB(sceneColor.rgb), 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 texel = 1. / iResolution.xy;

    float step_x = texel.x;
    float step_y = texel.y;
    vec2 n  = vec2(0.0, step_y);
    vec2 s  = vec2(0.0, -step_y);
    vec2 e  = vec2(-step_x, 0.0);
    vec2 w  = vec2(step_x, 0.0);

    vec3 im =    texture(iChannel0, uv).rgb;
    vec3 im_n =  texture(iChannel0, uv+n).rgb;
    vec3 im_s =  texture(iChannel0, uv+s).rgb;
    vec3 im_e =  texture(iChannel0, uv+e).rgb;
    vec3 im_w =  texture(iChannel0, uv+w).rgb;

    // North/South sort. Use luminance of Red and Blue
    if(int(mod(float(iFrame), 4.0)) < 2)
    {
        if(int(mod(float(iFrame) + fragCoord.y, 2.0)) == 0)
        {
            if ((luminanceRB(im_s) > luminanceRB(im)))
                im = im_s;
        }
        else
        {
            if ((luminanceRB(im_n) < luminanceRB(im)))
                im = im_n;
        }
    }
    // West, East sort. Just use Green
    else
    {
        if(int(mod(float(iFrame) + fragCoord.x, 2.0)) == 0)
        {
            if ((im_w.g > im.g))
                im = im_w;
        }
        else
        {
            if ((im_e.g < im.g))
                im = im_e;
        }
    }

    fragColor = vec4(im, 1.0);

    // initialize with image
    if(iFrame<10 || texelFetch(iChannel3, ivec2(32, 0), 0).x > 0.0)
    {
        fragColor = texture(iChannel1, uv*0.8);
    }
}

float luminance(vec3 color)
{
    return (0.2126 * color.r + 0.7152 * color.g + 0.0722 * color.b);
}

float luminanceRB(vec3 color)
{
    return (0.2126 * color.r + 0.0722 * color.b);
}
