void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cm = distance(iMouse.xy,fragCoord.xy)/iResolution.x;
 	vec2 center = iResolution.xy/2.;
    float dist = distance(center,fragCoord);
    float cl;
    float cr;
    float R = iResolution.x/2.*((sin(iTime/3.)+2.)/2.)+iResolution.x/10.;
    vec3 col=vec3((dist>R && dist<R+1.)?1.:0.);
    cl = sqrt((R*R)-pow(fragCoord.y-center.y,2.));
    cr = center.x+cl;
    cl = center.x-cl;
    cl = abs(fragCoord.x+cl)/iResolution.x;
    cr = abs(fragCoord.x+cr)/iResolution.x;
    fragColor = vec4(cm,cl,cr,1.0);
}
