float aaStep(float edge, float gradient)
{
    float halfPix = fwidth(gradient) / 2.0;
    float low = edge - halfPix;
    float hi  = edge + halfPix;
    return clamp((gradient - low) / (hi - low), 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
    float theta = sin(iTime) * 25.0 * (3.14159 / 180.0);
    uv = vec2(cos(theta) * uv.x - sin(theta) * uv.y,
              sin(theta) * uv.x + cos(theta) * uv.y);

	vec3 col;

    if (iMouse.z < 0.0) col = vec3(aaStep(0.5, uv.x));
    else                col = vec3(step(0.5, uv.x));

    fragColor = vec4(col.rgb,1.0);
}
