float disk(vec2 r, vec2 center, float radius) {
	return 1.0 - smoothstep( radius-0.5, radius+0.5, length(r-center));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
	vec2 p = vec2(fragCoord.xy / iResolution.xy);

    vec2 revert_p = p;
    if(revert_p.x >= 0.5 ){
        revert_p.x = revert_p.x * 0.5;
    }
    // 背景カラー
	vec3 bgCol = vec3(0.7); 

	vec2 center;
	center = iMouse.xy;
	float radius = 120.0;

    vec3 ret = texture(iChannel0, revert_p).xyz;
	ret = mix(ret, texture(iChannel2, p).xyz, disk(fragCoord.xy, center, radius));

	vec3 pixel = ret;
	fragColor = vec4(pixel, 1.0);
}
