#define step 0.01
#define cutoff 0.500

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 c = texture(iChannel0,uv);

    float gray = dot( c.rgb, vec3(0.399, 0.587, 0.314));

    vec4 grayV = vec4(vec3(gray), 1.0);

    for(float i=1.0;i>0.;i-=step)
    {
         if (grayV.g < i && grayV.r < i-cutoff && grayV.b < i-cutoff) 
             grayV=vec4(0.);
        //else
            //grayV = vec4(0.);//,0.1, 0.8,1.0);
    }

    fragColor = grayV;
}
