#define pi 3.1415926

float wave(float t)
{
    return sin(2.0 * pi * t);
}

float saw(float t)
{
    return 2.0 * fract(t) - 1.0;
}

float rect(float t)
{
    return 2.0 * step(0.5, fract(t)) - 1.0;
}

vec2 mainSound( in int samp, float time )
{
    float t = 0.25 * 440.0 * time;
    float h = exp(-3.0 * time);
    //return vec2(mix(rect(t), sin(t), h)) * h;
    return vec2(mix(wave(t), saw(t), h)) * h; // plucked string like sound
    //return vec2(sin(pi * 440.0*time)*exp(-3.0*time));
}
