vec2 hash2( vec2 p )
{
	return fract(sin(vec2(dot(p,vec2(237.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 halfres = .5 * iResolution.xy;
    vec2 uv = (fragCoord - halfres ) / iResolution.y;
    vec2 mouseP = (iMouse.xy - halfres ) / iResolution.y;

    float dist = 1.0 - length(uv - mouseP) * 6.0;

    uv *= 300.0;

    vec2 id = floor(uv);
    vec2 frac = fract(uv);

    if(dist > 0.0)
    {
        id = floor(uv * 0.25);
        frac = fract(uv* 0.25);
    }

    vec3 col = (1.0 - vec3(length(frac - 0.5) * 1.25)) * (dist * 2.0);
    col *= vec3(hash2(id).x * sin((5.0 + iTime) * hash2(id).x), cos((5.0 + iTime) * hash2(id)));
    col = pow(col, vec3(1.0 / 2.2));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
