vec4 circle(vec2 uvx, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uvx) - rad;
	float t = clamp(d, 0.0, 1.0);
	return vec4(color, 1.0 - t);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy;

	uv = uv*4.0;
	uv -= 2.0;

	float waveX1 = sin(uv.x * 2.0 + iTime*2.0) - uv.x*uv.y;
	float waveX2 = sin(uv.x * 4.0 + iTime*4.0) - uv.x*uv.y;
	float waveX3 = sin(uv.x * 6.0 + iTime*6.0) - uv.x*uv.y;

	vec4 waves = vec4(waveX1, waveX2 , waveX3, 1.0);

	vec2 center = iResolution.xy * 0.5;
	float radius = 0.25 * iResolution.y;


	vec3 white = vec3(1.0, 1.0, 0.6);
	vec4 layer = circle(fragCoord.xy, center, radius, white);

	vec4 color = mix(waves, layer, layer.a*waves*0.4);
	fragColor = color;
}
