#define N 16.0
//#define N 8.0
#define speed 2.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv.x += floor(speed * iTime);
    uv.y += (N - 1.0) - floor(speed * iTime / N);

    fragColor = texture(iChannel0, uv / N).xxxx;
}
