// Color Cycling Circle
// inspired by The Art of Code - Shadertoy Tutorials
// https://www.youtube.com/playlist?list=PLGmrMu-IwbguU_nY2egTFmlg691DN7uE5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy; 	// 0. <> 1.

    uv -= .5;									// -0.5 <> 0.5
    uv.x *= iResolution.x/iResolution.y;		// compress ellipse to circle (x axis only)

    vec3 color = vec3(1.);

    float d = length(uv);						// UV -= .5 represents the center of the screen
    float r = 0.3;								// radius of the circle
    float blur = 0.01;							// blur amount : higher values for more blur
    float mask = smoothstep(r, r-blur, d);		// create a mask with the shape pf of a blurry circle

    float red = 0.5 + 0.5*cos(iTime*0.6);
    float green = 0.5 + 0.5*cos(iTime*0.75);
    float blue = 0.5 + 0.5*cos(iTime*0.46);

    color = vec3(red,green,blue)*mask;

    fragColor = vec4(color, 1.0);				// draw a blurry circle
}
