struct rect {
  float f;
  vec3 color;
};

float drect(vec2 p, vec2 size, vec2 uv) {
    vec2 bl = step(p, uv);
    vec2 tr = step(1.0-size-p, 1.0-uv);

    return bl.x*bl.y*tr.x*tr.y;
}

rect newRect(vec2 p, vec2 size, vec2 uv, vec3 color) {
   rect r;
   r.f =  drect(p, size, uv);
   r.color = color;
   return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.0);
    vec3 red = vec3(1.0, 0.0, 0.0);
    vec3 hw  = vec3(0.8, 0.8, 0.8);
    vec3 yellow = vec3(1.0, 1.0, 0.0);
    vec3 blue = vec3(0.0, 0.0, 1.0);

    rect rects[17];
    rects[0] = newRect(vec2(0.0, .8), vec2(0.05, 0.2), uv, red);
    rects[1] = newRect(vec2(0.08, .8), vec2(0.1, 0.2), uv, red);      
    rects[2] = newRect(vec2(0.21, .8), vec2(0.55, 0.2), uv, hw);
    rects[3] = newRect(vec2(0.79, .8), vec2(0.15, 0.2), uv, hw);
    rects[4] = newRect(vec2(0.97, .8), vec2(0.03, 0.2), uv, yellow);

    rects[5] = newRect(vec2(0.0, .57), vec2(0.05, 0.2), uv, red);
    rects[6] = newRect(vec2(0.08, .57), vec2(0.1, 0.2), uv, red);      
    rects[7] = newRect(vec2(0.21, .57), vec2(0.55, 0.2), uv, hw);
    rects[8] = newRect(vec2(0.79, .57), vec2(0.15, 0.2), uv, hw);
    rects[9] = newRect(vec2(0.97, .57), vec2(0.03, 0.2), uv, yellow);

    rects[10] = newRect(vec2(0.0, 0.0), vec2(0.18, 0.54), uv, hw);

    rects[11] = newRect(vec2(0.21, 0.15), vec2(0.55, 0.39), uv, hw);
    rects[12] = newRect(vec2(0.79, 0.15), vec2(0.15, 0.39), uv, hw);
    rects[13] = newRect(vec2(0.97, 0.15), vec2(0.03, 0.39), uv, hw);

    rects[14] = newRect(vec2(0.21, 0.0), vec2(0.55, 0.12), uv, hw);
    rects[15] = newRect(vec2(0.79, 0.0), vec2(0.15, 0.12), uv, blue);
    rects[16] = newRect(vec2(0.97, 0.0), vec2(0.03, 0.12), uv, blue);

    for(int i=0;i<17;i++) {
      col += rects[i].f*rects[i].color;   
    }

    // Output to screen
    fragColor = vec4(col, 1.0);
}
