float va ( in vec2 _st) {
    float v = sin(iTime*0.1)*0.2;
    float a = 0.2;    
    for (int i = 0; i <3; ++i) {
        v += a;
        _st = _st * 2.0;
        a *= 2.0;
    }
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Wide-angle lens is baced on Menger Sponge Variation
    // https://www.shadertoy.com/view/ldyGWm
    vec3 st = (vec3(2.0 * fragCoord - iResolution.xy, iResolution.y));
    st = normalize(vec3(st.xy, sqrt(max(st.z * st.z - dot(st.xy, st.xy) *2.,0.)))); 
    //st *= 4.0*sin(u_time*0.1);

    vec2 coord = st.xy * 0.2;
    float len; 
    for (int i = 0; i < 3; i++) {
        len = length(vec2(coord.x, coord.y));  
        coord.x +=  sin(coord.y + iTime * 0.1)*2.1;
        coord.y +=  cos(coord.x + iTime * 0.05 + cos(len * 50.0))*1.;
    }
    len -= 3.;

    vec3 color = vec3(0.);

    color = mix(color, cos(len + vec3( 0.5, 0.0, 0.1)), 1.0);
    color = mix(vec3(0.595,0.490,0.241),vec3(0.420,0.042,0.038),color);

    float f = va(st.xy);
    fragColor = vec4((f*f*f+.6*f*f+.5*f)*color,1.);
}
