float uvrand(vec2 uv)
{
    return fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = gl_FragCoord.xy;

    const float pi = 3.1415926;
    float t = floor(iTime * 1.1) * 7.3962;

    vec2 sc = (coord.xy - iResolution.xy / 2.0) / iResolution.y;
    float phi = atan(sc.y, sc.x + 1e-6);
    vec2 pc = vec2(fract(phi / (pi * 2.f)), length(sc));  //極座標

    // 放射
    float h_divnum = 5. + 10.*abs(sin(float(int(iTime))));
    float s_divnum = 5. + 10.*abs(sin(float(int(iTime))));

    float ddth = fract(pc.x * h_divnum);

    // 放射区分けごとのランダム値
    float h_id = (pc.x * h_divnum) - fract(pc.x * h_divnum); 
    float h_rand = uvrand(vec2(h_id,int(iTime)));

    // 同心円区分けごとのランダム値
    float s_id  = (pc.y * s_divnum) - fract(pc.y * s_divnum); 
    int   is_id = int(s_id);   

    float s_rand = uvrand(vec2(float(s_id),int(iTime)));



    // 横線ゆがませ
    float l = pc.y+ ddth * (h_rand - 0.5) + ddth*(s_rand - 0.5)*0.2 ;

    // 放射・同心円

    float thickness = 0.5- pc.y + (s_rand*0.1 + h_rand*0.1) + (0.02*sin((h_rand)*iTime*10.f)); // 微妙

    float a = step(abs(sin( ( pc.x*pi*2.f  )  * s_divnum )),thickness * 0.5);
    float b = step(abs(sin( ( pc.y*pi*2.f + h_rand + l)  * h_divnum )),thickness*0.5 );

    fragColor = vec4(a+b,a+b,a+b,1.0);
}
