// https://www.shadertoy.com/view/4sKyzt
// https://fotw.info/flags/fic%5Ejerc.html
// https://www.imdb.com/title/tt0805663/

vec4 stars(float r, float q, vec2 U, float padL, float padR)
{
    return vec4(1,1,0,0) * smoothstep(r, 0.,
                   length(U = vec2(

                       fract(clamp(U.x + .7, padL, padR)),

                       .0 + mod(clamp(U.y - 2.0, 8. - q, 14. + q ) - q * .8, 1.62)) - .5)

                   * cos(abs(

                       mod( atan(U.x, U.y) 
                       // rotation
                       + .6, 

                       // spikes
                       1.26

                       // rotation
                   ) - .6) 

                         - 1.26) 
                                              - .1);
}

void mainImage(out vec4 O, vec2 U)
{
    float r = 17. / iResolution.y,
          q = mod(ceil((U *= r).x - .3), 2.);

    O = 
        (U.x < 14.01 && U.y > 9. ? 
         // blue border

         U.y > 15.9 ?  vec4(0,0,1,0)  : 

         U.y > 15.0 ?  vec4(0,0,1,0) + stars(r, q, U, 6.0, 9.0) : // upper 2
         U.y > 11.1 ?  vec4(0,0,1,0) + stars(r, q, U, 4.0, 11.0) : // ok
         U.y > 10.1 ?  vec4(0,0,1,0) + stars(r, q, U, 6.0, 9.0) : // lower 2
         vec4(0,0,1,0) 

         : 
         // stripes
         sin(2.69 * U.x * 0.5) < 0. ? vec4(1,1,1,0): vec4(1,0,0,0));


}
